package patterns;

public class Pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        char i;
        char j;
        for(i='E';i>='A';i--)
        {
        	for(j='E';j>='A';j--)
        	{
        		System.out.print(i);//EEEEE
        	}
        	System.out.println();
        }
	}

}
